mapper.select:
SELECT * FROM board
ORDER BY seq_board DESC
LIMIT #{startRow}, #{endRow}

SELECT * FROM board
ORDER BY hit DESC
LIMIT #{startRow}, #{endRow}

mapper.update:
UPDATE board SET 
imageFileName = #{imageFileName}, 
imageOriginalFileName = #{imageOriginalFileName}, 
imageSubject = #{imageSubject}, 
imageContent = #{imageContent} 
WHERE seq_board = #{seq_board}

UPDATE board SET
imageSubject = #{imageSubject}, 
imageContent = #{imageContent} 
WHERE seq_board = #{seq_board}
